#include <stdio.h>
#include <time.h>

void print_list(int *list, int size) {
    printf("{");
    for (int i = 0; i < size - 1; i++) {
        printf("%d, ", list[i]);
    }
    printf("%d}\n", list[size - 1]);
}

void shorting_list(int *list, int size) {
    for (int i = 0; i < size - 1; i++) {
        int min = i;
        for (int j = i + 1; j < size; j++) {
            if (list[j] < list[min]) {
                min = j;
            }
        }
        if (min != i) {
            int a = list[min];
            list[min] = list[i];
            list[i] = a;
        }
    }
}

int binary_search(int *list, int size, int target) {
    int L = 0;
    int R = size - 1;
    int m;
    while (L <= R) {
        m = (L + R) / 2;
        if (list[m] < target) {
            L = m + 1;
        }
        else if (list[m] > target) {
            R = m - 1;
        }
        else {
            return m;
        }
    }
    return -1;
}

